import React, { useState, useEffect } from 'react';
import { createProduct, updateProduct } from '../services/api';


export default function ProductForm({ productToEdit, onSave, onCancel }) {
  const [product, setProduct] = useState({
    nome: '',
    descricao: '',
    preco: '',
    url_imagem: '',
    categoria: '',
  });

  useEffect(() => {
    if (productToEdit) {
      setProduct(productToEdit);
    } else {
      setProduct({
        nome: '',
        descricao: '',
        preco: '',
        url_imagem: '',
        categoria: '',
      });
    }
  }, [productToEdit]);

  function handleChange(e) {
    const { name, value } = e.target;
    setProduct(prev => ({ ...prev, [name]: value }));
  }

  async function handleSubmit(e) {
  e.preventDefault();

  
  try {
      const productToSend = {
      ...product,
      preco: parseFloat(product.preco), // <- convertendo aqui
    };
    if (product.id || product.idproduto) {
      await updateProduct(product.id || product.idproduto, product);
    } else {
      await createProduct(product);
    }
    onSave();  // avisa que salvou para atualizar a lista e esconder o form
  } catch (error) {
    alert('Erro ao salvar produto');
    console.error(error);
  }
}

  return (
    <form onSubmit={handleSubmit}>
      <input name="nome" value={product.nome} onChange={handleChange} placeholder="Nome" required />
      <input name="descricao" value={product.descricao} onChange={handleChange} placeholder="Descrição" required />
      <input name="preco" value={product.preco} onChange={handleChange} placeholder="Preço" required />
      <input name="url_imagem" value={product.url_imagem} onChange={handleChange} placeholder="URL da Imagem" />
      <input name="categoria" value={product.categoria} onChange={handleChange} placeholder="Categoria" />
      <button type="submit">Salvar</button>
      <button type="button" onClick={onCancel}>Cancelar</button>
    </form>
  );
}
